// Timeline data - Kuchipudi history milestones
const timelineData = [
    {
        year: "1500s",
        title: "Origins in Andhra Pradesh",
        content: "Kuchipudi originated in the village of Kuchelapuram in Andhra Pradesh, evolving from traditional dance dramas.",
        image: "data:image/jpeg;base64,",
        details: "The dance form was originally performed by men of the Brahmin community as a form of storytelling, often depicting stories from Hindu epics like the Mahabharata and Ramayana."
    },
    {
        year: "1675",
        title: "Siddhendra Yogi's Contributions",
        content: "The saint Siddhendra Yogi formalized and systematized Kuchipudi dance.",
        image: "data:image/jpeg;base64,",
        details: "Siddhendra Yogi, considered the father of Kuchipudi, composed the 'Bhama Kalapam', a dance drama about Satyabhama, which became a cornerstone of the Kuchipudi repertoire."
    },
    // Add more timeline items...
    {
        year: "1950s",
        title: "Transition to Stage Performance",
        content: "Kuchipudi began transitioning from village performances to concert stage art.",
        image: "data:image/jpeg;base64,",
        details: "Under the guidance of gurus like Vempati Chinna Satyam, Kuchipudi evolved from its traditional village square performances to a sophisticated stage art, with women beginning to perform the dance form."
    },
    {
        year: "Present",
        title: "Global Recognition",
        content: "Kuchipudi has gained international recognition as a classical Indian dance form.",
        image: "data:image/jpeg;base64,",
        details: "Today, Kuchipudi is performed worldwide, with schools and teachers spreading the art form across continents while maintaining its traditional roots."
    }
];

// Initialize the timeline
document.addEventListener('DOMContentLoaded', function() {
    const timeline = document.querySelector('.timeline');
    
    // Create timeline items
    timelineData.forEach((item, index) => {
        const timelineItem = document.createElement('div');
        timelineItem.className = 'timeline-item';
        timelineItem.innerHTML = `
            <div class="timeline-year">${item.year}</div>
            <h3 class="timeline-title">${item.title}</h3>
            <p class="timeline-content">${item.content}</p>
            <img src="${item.image}" alt="${item.title}" class="timeline-image">
        `;
        
        // Add click event to show details
        timelineItem.addEventListener('click', () => {
            showDetails(item);
        });
        
        timeline.appendChild(timelineItem);
    });

    // Scroll animations
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('visible');
            }
        });
    }, { threshold: 0.1 });

    document.querySelectorAll('.timeline-item').forEach(item => {
        observer.observe(item);
    });
});

// Show detailed view
function showDetails(item) {
    const detailContainer = document.getElementById('detail-view');
    detailContainer.innerHTML = `
        <h2>${item.title} (${item.year})</h2>
        <img src="${item.image}" alt="${item.title}" class="detail-image">
        <p>${item.details}</p>
        <button class="close-btn">Close</button>
    `;
    
    detailContainer.style.display = 'block';
    
    // Add close button functionality
    detailContainer.querySelector('.close-btn').addEventListener('click', () => {
        detailContainer.style.display = 'none';
    });
}